import React, { useState } from 'react';
import { Button, Drawer } from 'antd';
import './Hamburger.css'
import { Input } from "@material-ui/core";
import {Link} from 'react-router-dom'
import Popup from "./Popup";



const Hamburger = (props) => {

    const [open, setOpen] = useState(false);
  return (
    <>
      <Drawer className='contact-drawer' placement="right" onClose={props.onClose} open={props.open}>
        <div>
            <ul className='Mobile-Router'>
                <li className="Router-Home" onClick={() => setOpen(false)}><Link to='/'><button className='Button-Hamburger'>Home</button></Link></li>
                <li className="Router-Projects"><Link to='/Projects'><button className='Button-Hamburger'>Projects</button></Link></li>
                <li className="Router-About"><Link to='/About'><button className='Button-Hamburger'>About</button></Link></li>
                <li className="Router-Contact"><button className='Button-Hamburger' onClick={() => setOpen(true)}> Contact</button></li>
            </ul>
            <div className='BottomButtons'>
                    <button className='BottomButton'>Linkedin</button>
                    <button className='BottomButton'>Instagram</button>
                    <button className='BottomButton'>Behance</button>
            </div>
        </div>
      </Drawer>
      <Popup open={open} onClose={() => setOpen(false)} />

    </>
  );
};
export default Hamburger;
