import "./App.css";
import Home from "./Home";
import About from "./About";
import { useEffect, useState } from "react";
import Navbar from "./components/NavigationBar.js";
import Services from "./components/Services"
import Experiences from './components/Experiences'
import Footer from "./components/FooterCom";
import GoogleFont from 'react-google-fonts';
import { motion } from "framer-motion";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Project1 from './components/Project1.jpg';
import Project2 from './components/Project2.jpg';
import Project3 from './components/Project3.jpg';
import Project4 from './components/Project4.jpg';
import Project5 from './components/Project5.jpg';
import Pics from './Pics.jpg';
import { duration } from "@material-ui/core";


function App() {

  const [mousePosition, setMousePosition] = useState({
    x: 0,
    y: 0
  });
  const [cursorVariant, setCursorVariant] = useState("default");


  useEffect(() => {
    const mouseMove = e => {
      setMousePosition({
        x: e.clientX,
        y: e.clientY
      })
    }

    window.addEventListener("mousemove", mouseMove);

    return () => {
      window.removeEventListener("mousemove", mouseMove);
    }
  }, []);

  const variants = {
    default: {
      x: mousePosition.x - 8,
      y: mousePosition.y - 8,
    },
    wide: {
      width: 100,
      height: 100,
      x: mousePosition.x - 50,
      y: mousePosition.y - 50,
      backgroundColor: 'white'
    }

    }

  const imgEnter = () => setCursorVariant('wide')
  const imgLeave = () => setCursorVariant('default')


  return (
    <div className="App" >

      <motion.div className='Cursor'
        variants={variants}
        animate='default'
        transition={{duration:0.06}}
      />


      <motion.div className='SecondCursor'
        variants={variants}
        animate={cursorVariant}
        transition={{duration:0.01}}
      />


      <GoogleFont font='Manrope' />

          <Router>
          <div className='AppNavbar'>
            <Navbar/>
          </div >

          <div className="Content">
            <Routes>

              <Route exact path="/">
                <Home/>
              </Route>

              <Route exact path="/About">
                <About/>
              </Route>


            </Routes>

          </div>



          <div className= 'footer' style={{backgroundColor:'#191919', paddingLeft:40, paddingRight:40, paddingTop:120}}>

              <Footer/>

          </div>

          </Router>


    </div>
  );
}

export default App;
