import React, { useEffect, useState } from "react";
import Experiences from "./Experiences";
import { useSearchParams } from "react-router-dom";

function ProjectDetails () {

  const [project, setProject] = useState({});
  const [searchParams, setSearchParams] = useSearchParams();

    useEffect( () =>{
        async function fetchData() {
          const data = await fetch("/assets/projects.json").then(data => data.json());
          setProject(data.find(proj => proj.id.toString() === searchParams.get("id")));
        }
        fetchData();

      }, []);
    return (
       <div>
            <div className='HeaderPicDiv' style={{height:400, color:''}}>
                <img src={project.img} className='HeaderPic' style={{height:400}}/>
            </div>

            <div>
                <div className='Client Name'><p>{project.clientname}</p></div>
                <div className='Services'>
                    <p>{project.service1}</p>
                    <p>{project.service2}</p>
                    <p>{project.service3}</p>
                </div>
                <div className='Project Year'><p>{project.year}</p></div>
            </div>

            <div>
                <div className='ProjectNameDiv'>
                    <h3>{project.name}</h3>
                </div>
                <div>
                    <Experiences name='Client' year={project.clientname}/>
                    <Experiences name='Year' year={project.year}/>
                    <Experiences name='Scope of the Work' year={project.scope}/>
                    <Experiences name='Location' year={project.location}/>

                </div>
                <div>
                    {project.explanation}
                </div>
            </div>
            <div>
                <div className='Projet Pictures'>
                    <img src={project.img1}/>
                    <img src={project.img2}/>
                </div>
                <div className='Projet Pictures'>
                    <img src={project.img3}/>
                </div>
                <div className='Projet Pictures'>
                    <img src={project.img4}/>
                    <img src={project.img5}/>
                </div>
            </div>
            <div>

            </div>      
       </div> 
    )
}

export default ProjectDetails
