import "./App.css";
import './smallscreen.css'
import Home from "./Home";
import About from "./About";
import Projects from "./Projects";
import Hamburger from "./components/Hamburger";
import { useEffect, useState } from "react";
import Navbar from "./components/NavigationBar.js";
import Footer from "./components/FooterCom";
import GoogleFont from 'react-google-fonts';
import { motion } from "framer-motion";
import { duration } from "@material-ui/core";
import { Routes , Route } from 'react-router-dom'
import ProjectDetails from "./components/ProjectDetails";
import MenuRoundedIcon from '@mui/icons-material/MenuRounded';



function App() {

  const [mousePosition, setMousePosition] = useState({
    x: 0,
    y: 0
  });
  const [cursorVariant, setCursorVariant] = useState("default");

  const [open, setOpen] = useState(false);


  useEffect(() => {
    const mouseMove = e => {
      setMousePosition({
        x: e.clientX,
        y: e.clientY
      })
    }

    window.addEventListener("mousemove", mouseMove);

    return () => {
      window.removeEventListener("mousemove", mouseMove);
    }
  }, []);

  const variants = {
    default: {
      x: mousePosition.x - 8,
      y: mousePosition.y - 8,
    },
    wide: {
      width: 100,
      height: 100,
      x: mousePosition.x - 50,
      y: mousePosition.y - 50,
      backgroundColor: 'white'
    }}

  const variants2 = {
      default: {
        x: mousePosition.x - 16,
        y: mousePosition.y - 16,
      },

    }



  return (
    <div className="App" >

      <motion.div className='Cursor'
        variants={variants}
        animate='default'
        transition={{duration:0.05}}
      />


      <motion.div className='SecondCursor'
        variants={variants2}
        animate={cursorVariant}
        transition={{duration:0.1}}
      />


      <GoogleFont font='Manrope' />

          <div className='AppNavbar'>
            <div>
            <Navbar/>
            </div>
            <div className='Hamburger'>
             <button className='HamburgerButton' onClick={() => setOpen(true)}><MenuRoundedIcon/></button>
            </div>

          </div >

          <Hamburger HamburgerOpen={open} onClose={() => setHamburgerOpen(false)} />

          <div className='content'>
            <Routes>
              <Route exact path="/" element={<Home/>} />
              <Route exact path="/Projects" element={<Projects/>} />
              <Route exact path="/About" element={<About/>} />
              <Route exact path='/ProjectDetails' element={<ProjectDetails/>} />
            </Routes>
          </div>




          <div className= 'footer'>

              <Footer/>

          </div>


    </div>
  );
}

export default App;
